import Axios from 'axios'
import React, {useState, useEffect} from "react";
import { useDispatch, useSelector} from 'react-redux';
import { fetchTeam } from '../actions/fetchTeam'
import interceptor from "../interceptor/interceptor"
import Card from "./employeeCard";

const TeamSelector = () => { 


    const teams = useSelector((state) => state.teamsReducer)

    const[selectedTeam, setSelectedTeam] = useState({
        id: 1
    })

    const dispatch = useDispatch();

    useEffect(() => {

        dispatch(fetchTeam(selectedTeam.id))
        getMembers()
    }, [selectedTeam])

    function handleChange(event) {
        const value = event.target.value;
        setSelectedTeam({
            ...selectedTeam,
            [event.target.name]: value
        });
    }

    const [team, setTeam] = useState()

    const getMembers = () => {

        Axios.get(`http://localhost:8080/api/team/read/squad/${selectedTeam.id}`)
        .then(res => { setTeam(res.data)})
        .catch(err => console.log(err))
    }

    const renderTeam = () => {


        return(
            team.map((member) => {
                return(
                    <Card member={member} />
                )
            })
        )
    }

    return(
        <table className="tables">
            <thead>
                <select 
                        name="id" 
                        className="controlsElements"
                        onChange={handleChange}
                    >
                        {
                            teams.map((team) => {return(<option value={team.id}>{team.id}</option>)})
                        }
                </select>
            </thead>
            <tbody>
                <div className="cards-wrapper">
                        {team === (null || undefined) ? "SORRY" : renderTeam()}
                </div>


            </tbody>

        </table>

    )
}

export default TeamSelector
