import Axios from 'axios'
import React, {useCallback} from 'react'
import { useSelector} from 'react-redux'
import {useDropzone} from 'react-dropzone'
import interceptor from "../interceptor/interceptor"
import { FaAngleDown, FaAngleDoubleDown } from "react-icons/fa";


const Dropzone = () => {

  const {id, imageUrl} = useSelector((state) => state.loggedUserReducer)

    const onDrop = useCallback(acceptedFiles => {
      const file = acceptedFiles[0]
      console.log(file)

      const formData = new FormData();
      formData.append("file", file)

      console.log(id)

      Axios.post(`http://localhost:8080/api/images/1/image/upload`, formData, 
      {
        headers: {
            "Content-Type": "multipart/form-data"
        }
      }
    ).then(() => console.log("File uploaded succesfuly"))
    .catch(err => console.log(err))
  }, [])

  const {getRootProps, getInputProps, isDragActive} = useDropzone({onDrop})

  return (
    <div className="profile-dropDown-image-wrapper" {...getRootProps()}>
      <input className="profile-dropDown-image-drag" {...getInputProps()} />
      {
        isDragActive ?
        <FaAngleDoubleDown className="dropIcon">    <path
        d="M12 21.35l-1.45-1.32C5.4 15.36 2 12.28 2 8.5 2 5.42 4.42 3 7.5 3c1.74 0 3.41.81 4.5 2.09C13.09 3.81 14.76 3 16.5 3 19.58 3 22 5.42 22 8.5c0 3.78-3.4 6.86-8.55 11.54L12 21.35z"
        stroke="white"
        strokeWidth={1}
      /></FaAngleDoubleDown> :
        <FaAngleDown className="dropIcon">    <path
        d="M12 21.35l-1.45-1.32C5.4 15.36 2 12.28 2 8.5 2 5.42 4.42 3 7.5 3c1.74 0 3.41.81 4.5 2.09C13.09 3.81 14.76 3 16.5 3 19.58 3 22 5.42 22 8.5c0 3.78-3.4 6.86-8.55 11.54L12 21.35z"
        stroke="white"
        strokeWidth={1}
      /></FaAngleDown>
      }
      {
          imageUrl ? <img className="profile-dropDown-image" src={`http://localhost:8080/api/images/1/image/download`} alt={""}/> : ''
      }
    </div>
  )
}

export default Dropzone;
