import React, {useState} from "react";
import { Form, Field } from 'react-final-form'
import { useHistory } from "react-router-dom";
import  { Redirect } from 'react-router-dom'
import Axios from "axios";
import "../interceptor/interceptor"

const Login = () => {

    const history = useHistory();
    const [credentials, setCredentials] = useState({username: '', password: ''})

    const handleChange = (event) => {
        const value = event.target.value;
        setCredentials({
            ...credentials,
            [event.target.name]: value
        });
        console.log(credentials)
    };

    const handleLoginSubmit = async (event) => {

    event.preventDefault()

     await authorize();

    }

    const handleRegistrationSubmit = values => {
      }

    const authorize =  () => {
         Axios.post("http://localhost:8080/login", 
            {
            username: credentials.username,
            password: credentials.password
            }
        )
        .then(response => {
            if (response.status === 200) {
                //TODO Store it in a cookie
                localStorage.setItem("authorization", response.data.jwt);
                localStorage.setItem('loggedUser', credentials.username);
                history.go("/home")

            }
        }).catch(error => {
        console.error(error)
        })
    }

    const loginForm = () => {
        return(
            <form className="loginFormWrapper" onSubmit={event => handleLoginSubmit(event)}>
                <label className="loginFormLabels">EMAIL</label>
                <input className="loginFormInput" type="text" name="username" placeholder="" value={credentials.username} onChange={event => handleChange(event)}/>
                <label className="loginFormLabels"> PASSWORD</label>
                <input className="loginFormInput" type="password" name="password" placeholder="" value={credentials.password} onChange={event => handleChange(event)}/>

                <div className="loginButtonsWrapper">
                <button className="forgottenPasswordFormButton" type="submit" value="Submit">FORGOTTEN PASSWORD?</button>

                <button className="signInFormButton" type="submit" value="Submit">SIGN IN</button>
                <button className="signInFormButton" type="button" value="Submit">SIGN UP</button>
                </div>
            </form>
        )
    }

    const registerForm = () => {
        return(
            <Form
            onSubmit={handleRegistrationSubmit}
            render={({ onSubmit, form, values }) => (
              <form 
                onSubmit={onSubmit}
                className="registrationFormWrapper"
              >
                  <div className="className="loginFormInput>

                    <label className="formLabel">First Name:</label>
                    <Field
                      className="formTextInput formTextServiceInput" 
                      name="firstName"
                      component="input"
                      type="text"
                    />
                    <label className="formLabel">Last Name:</label>
                    <Field
                      className="formTextInput formTextServiceInput" 
                      name="lastName"
                      component="input"
                      type="text"
                    />
                    <label className="formLabel">Email:</label>
                    <Field
                      className="formTextInput formTextServiceInput" 
                      name="email"
                      component="input"
                      type="email"
                    />
                    <label className="formLabel">Days Off Left:</label>
                    <Field

                      className="formTextInput formTextServiceInput" 
                      name="daysOffLeft"
                      component="input"
                      type="number"
                      min="0"
                      max="52"
                    />
                  <label className="formLabel">Role Type:</label>
                    <Field
                        className="formTextInput formTextServiceInput" 
                        name="roleType"
                        component="select"
                    >
                        <option selected value="EMPLOYEE">EMPLOYEE</option>
                        <option value="TEAM_LEADER">TEAM LEADER</option>
                        <option value="HR">HR</option>
                        <option value="ADMIN">ADMIN</option>
                    </Field>
                </div>
                <div className="formButtonsWrapper">
                    <button
                        className="formButton"
                        type="button"
                        onClick={form.reset}
                    >
                        RESET
                    </button>
                    <button 
                        type="submit"
                        className={`formButton formServiceButton`}
                    >
                        ADD
                    </button>

                </div>
              </form>
            )}
          />
        )
    }

    if(localStorage.getItem("authorization")!=null){
        return <Redirect go to='/home'  />
    } else {
        return(
            <div className="loginSiteWrapper">
                {loginForm()}
                {registerForm()}
            </div>
        )
    }
}

export default Login;
