import React, {useState, useEffect} from "react";
import { useDispatch, useSelector} from 'react-redux';
import { fetchTeam } from '../actions/fetchTeam'

const TeamSelector = (teams) => { 


    const[selectedTeam, setSelectedTeam] = useState()
    const dispatch = useDispatch();
    const team = useSelector((state) => state.teamReducer)


    useEffect(() => {
        dispatch(fetchTeam({id: selectedTeam}))
    }, [])

    function handleChange(event) {
        const value = event.target.value;
        setSelectedTeam({
            ...selectedTeam,
            [event.target.name]: value
        });
    }


    return(
        <table className="tables">
            <thead>
                <select 
                        name="sortOrder" 
                        className="controlsElements"
                        onChange={handleChange}
                    >
                        {
                            teams.teams.map((team) => {return(<option value={team.id}>{team.id}</option>)})
                        }
                </select>
            </thead>
            <tbody>
                        {console.log(team)}
            </tbody>

        </table>

    )
}

export default TeamSelector
