var Axios = require("axios");
import { useSelector} from 'react-redux';

const {id} = useSelector((state) => state.loggedUserReducer)

Axios.interceptors.request.use(
    function(config) {
        config.headers["loggedUser"] = id;
      return config;
    },
    function(err) {
      return Promise.reject(err);
    }
  );
