import { Form, Field } from 'react-final-form'
import { useDispatch} from 'react-redux';
import { useHistory } from "react-router-dom";
import { updateObject } from '../actions/updateObject'

const UpdateUser = ({entity}) => {

    const dispatch = useDispatch();    
    const history = useHistory();

    const onSubmit = values => {
      history.go("/home")
      dispatch(updateObject({object: "team", data: values}))
    }

    return (
        <Form
        onSubmit={onSubmit}
        initialValues={
            {
                leader: entity.teamLeaderFirstName + " " + entity.teamLeaderLastName,
                name: entity.name,
                id: entity.id,
                teamLeaderId: entity.firstName, 
            }

//             id(pin):2
// name(pin):"guacamole"
// teamLeaderId(pin):4
// teamLeaderFirstName(pin):"Wojciech"
// teamLeaderLastName(pin):"Wojski"
// teamLeaderEmail(pin):"wojciech@gmail.com"
        }
        render={({ handleSubmit, form, values }) => (
          <form 
            onSubmit={handleSubmit}
          >
            <div className="formFieldsWrapper">
              <div className="formFieldWrapper">
                <Field
                  name="id"
                  component="input"
                  type="hidden"
                />
              </div>
              <div className="formFieldWrapper">
                <label className="formLabel">First Name:</label>
                <Field
                  className="formTextInput formTextUpdateInput" 
                  name="firstName"
                  component="input"
                  type="text"
                />
              </div>
              <div className="formFieldWrapper">
                <label className="formLabel">Last Name:</label>
                <Field
                  className="formTextInput formTextUpdateInput" 
                  name="lastName"
                  component="input"
                  type="text"
                />
              </div>
              <div className="formFieldWrapper">
                <label className="formLabel">Email:</label>
                <Field
                  className="formTextInput formTextUpdateInput" 
                  name="email"
                  component="input"
                  type="email"
                />
              </div>
            </div>
            <div className="formButtonsWrapper">
                <button
                    className="formButton"
                    type="button"
                    onClick={form.reset}
                >
                    RESET
                </button>
                <button 
                    type="submit"
                    className={`formButton formUpdateButton`}
                >
                    UPDATE
                </button>

            </div>
          </form>
        )}
      />
    )


}
export default UpdateUser;
